<script type="text/javascript">
//Affichage dynamique titre menu left
$(function(){
	var $tabs = $('#menugauche');
	$tabs = $tabs.find('a');

	$tabs.click(function(){
		$elem = $(this);
		$val = $elem.attr('data-titre');
		$('#title').hide().html($val).fadeIn();
	}); 
});	 
</script><!-- End script-->


